mapper.select:
select dd.cu_id,CU.name as cu_name,dd.open_time as day_time, sum(case when dd.WORK_TYPE='1' THEN 1 ELSE 0 END) as num_emp, sum(case when dd.WORK_TYPE='2' THEN 1 when dd.WORK_TYPE='3' THEN 1 when dd.WORK_TYPE='4' THEN 1 ELSE 0 END) as num_temp, sum(case when dd.WORK_TYPE='5' then 1 else 0 end) as num_other from ( SELECT aa.cu_id, aa.open_time, bb.work_type, aa.card_id FROM ( SELECT DISTINCT sub.cu_id, date_format(door.OPEN_TIME, '%Y-%m-%d') open_time, door.card_id FROM np_dt_door_inout door FORCE INDEX ( IDX_DOOR_INOUT_1, IDX_DOOR_INOUT_2 ) INNER JOIN ( SELECT sub.id, sub.controlunitid as cu_id FROM NP_CF_SUBSYSTEM sub INNER JOIN np_sys_controlunit CU ON sub.CONTROLUNITID = CU.ID WHERE CU.FULL_PATH like CONCAT(#{fullPath},'%' )
) sub ON sub.id = door.subsystem_id WHERE door.OPEN_TIME >= #{startDate} AND door.OPEN_TIME < #{endDate} AND door.card_id IS NOT NULL ) aa INNER JOIN ( SELECT card.id, card.subsystem_id, wtp.work_type FROM np_cd_card card FORCE INDEX (IDX_CARD_1) INNER JOIN np_sys_controlunit CU ON card.CONTROLUNITID = CU.ID
INNER JOIN np_sys_person person ON person.ID = card.PERSON_ID INNER JOIN ( SELECT controlunitid, person_code, sum(work_type)-5 as work_type FROM np_sys_person group by controlunitid,person_code having (sum(work_type)-5)>0 and (sum(work_type)-5)<=5 ) wtp ON person.PERSON_CODE = wtp.person_code and person.controlunitid = wtp.controlunitid WHERE CU.FULL_PATH like CONCAT(#{fullPath},'%' ) ) bb ON aa.card_id = bb.id GROUP BY aa.open_time, bb.work_type, aa.card_id ) dd LEFT JOIN np_sys_controlunit cu on CU.id =dd.cu_id group by dd.cu_id,dd.open_time
